import React, { useState } from "react";
import { Breadcrumb, Row } from "antd";
import { notification } from "antd";
import { SmileOutlined, MehOutlined } from "@ant-design/icons";

import { ItemCard } from "./Cards";
import getJeans from "../Products/Jeans";
import heartUnFilled from "../../assets/icons/heart.png";
import heartFilled from "../../assets/icons/heart-filled.png";

function JeansItem() {
  const [message, setMessage] = useState("");
  const products = getJeans();

  const openNotification = (placement, prop) => {
    notification.open({
      message: prop.message,
      // icon:
      //   prop.heart === heartFilled ? (
      //     <SmileOutlined style={{ color: "#108ee9" }} />
      //   ) : (
      //     <MehOutlined style={{ color: "#108ee9" }} />
      //   ),
      placement,
    });
  };

  const like = (setHeart) => {
    // alert(heart);
    setHeart(heartFilled);
    setMessage("Added to WishList..");
  };

  const dislike = (setHeart) => {
    // alert(heart);
    setHeart(heartUnFilled);
    setMessage("Removed From Wishlist..");
  };

  const handlePay = (id, img, title, price, heart, setHeart) => {
    heart === heartFilled ? dislike(setHeart) : like(setHeart);
