    // alert(`id: ${id}, title: ${title}, price: ${price}`);
  };

  return (
    <div
      className="site-layout-background col-lg-12 p-4"
      style={{
        backgroundColor: "#e3e4e6",
      }}
    >
      <div className="mb-3" style={{ fontWeight: "600" }}>
        <Breadcrumb separator=">">
          <Breadcrumb.Item>Products</Breadcrumb.Item>
          <Breadcrumb.Item href="/products">Mens Shopping</Breadcrumb.Item>
          <Breadcrumb.Item>Jeans Category</Breadcrumb.Item>
        </Breadcrumb>
      </div>
      <Row gutter={10}>
        {products.map((val) => {
          return (
            <ItemCard
              key={val.itemId}
              id={val.itemId}
              img={val.itemImage}
              title={val.itemTitle}
              price={val.itemPrice}
              sizes={val.sizes}
              heartType={val.isWished ? heartFilled : heartUnFilled}
              callback={handlePay}
            />
          );
        })}
      </Row>
    </div>
  );
}

export default JeansItem;
