import React, { useState } from "react";

function CrouselComp(props) {
  const [heart, setHeart] = useState(props.item.heart);
  console.log(props.item.isWished, props.item.title, heart);
  return (
    <>
      <div>
        <h3
          style={{
            height: "300px",
            background: `url(${props.item.img})`,
            backgroundPosition: "center",
            backgroundSize: "cover",
            backgroundRepeat: "no-repeat",
          }}
        >
          <div className="hide logoCont">
            <img
              src="https://img.icons8.com/bubbles/40/fa314a/buy.png"
              className="logo1"
              style={{ borderRadius: "50%" }}
              onClick={() =>
                props.item.callback(
                  props.item.id,
                  props.item.img,
                  props.item.title,
                  props.item.price
                )
              }
            />
            <div style={{ flex: 1 }} />
            <img
              src={heart}
              className="logo1"
              style={{ borderRadius: "50%" }}
              onClick={() =>
                props.item.callback(
                  props.item.id,
                  props.item.img,
                  props.item.title,
                  props.item.price
                )
              }
            />
          </div>
        </h3>
      </div>
    </>
  );
}

export default CrouselComp;
