import React, { useState } from "react";
import { Breadcrumb, Row } from "antd";
import { notification } from "antd";
import { SmileOutlined, MehOutlined } from "@ant-design/icons";

import { ItemCard } from "./Cards";
import getJeans from "../Products/Jeans";

function JeansItem() {
  const [isWished, setIsWished] = useState({ set: false, message: "" });
  const products = getJeans();

  const openNotification = (placement, isWished) => {
    notification.open({
      message: isWished.message,
      icon: isWished.set ? (
        <SmileOutlined style={{ color: "#108ee9" }} />
      ) : (
        <MehOutlined style={{ color: "#108ee9" }} />
      ),
      placement,
    });
  };

  const dislike = (heart, setHeart) => {
    alert(heart);
    setHeart("https://img.icons8.com/nolan/38/like.png");
    setIsWished({ set: false, message: "Removed From WishList.." });
  };

  const like = (heart, setHeart) => {
    alert(heart);
    setHeart("https://img.icons8.com/fluent/38/000000/like.png");
    setIsWished({ set: true, message: "Added To Wishlist.." });
  };

  const handlePay = (id, img, title, price, heart, setHeart) => {
    heart === "https://img.icons8.com/fluent/38/000000/like.png"
      ? dislike(heart, setHeart)
      : like(heart, setHeart);
    openNotification("bottomRight", isWished);
    // alert(`id: ${id}, title: ${title}, price: ${price}`);
  };

  return (
    <div
      className="site-layout-background col-lg-12 p-4"
      style={{
        backgroundColor: "#e3e4e6",
      }}
    >
      <div className="mb-3" style={{ fontWeight: "600" }}>
        <Breadcrumb separator=">">
          <Breadcrumb.Item>Products</Breadcrumb.Item>
          <Breadcrumb.Item href="/products">Mens Shopping</Breadcrumb.Item>
          <Breadcrumb.Item>Jeans Category</Breadcrumb.Item>
        </Breadcrumb>
      </div>
      <Row gutter={10}>
        {products.map((val, key) => {
          return (
            <ItemCard
              key={val.itemId}
              id={val.itemId}
              img={val.itemImage}
              title={val.itemTitle}
              price={val.itemPrice}
              sizes={val.sizes}
              heart={
                val.isWished
                  ? "https://img.icons8.com/fluent/38/000000/like.png"
                  : "https://img.icons8.com/nolan/38/like.png"
              }
              callback={handlePay}
            />
          );
        })}
      </Row>
    </div>
  );
}

export default JeansItem;
