import React from "react";
import { Card, Carousel, Col, Row } from "antd";

const crousalContent = {
  height: "300px",
  backgroundPosition: "center",
  backgroundSize: "cover",
  backgroundRepeat: "no-repeat",
};

export function IntroCard(props) {
  return (
    <div className="cardBox">
      <div className="iconBox">
        <img src={props.icon} className="icon" />
      </div>
      <div className="cardTitle">{props.title}</div>
      <div className="cardIntro">{props.intro}</div>
    </div>
  );
}

export function CategoryCard(props) {
  return (
    <div
      className="cardBox2 col-lg-3 col-sm-5 m-2"
      style={{
        backgroundImage:
          "linear-gradient(rgba(0, 0, 0, 0.426), rgba(0, 0, 0, 0.426)),url(" +
          props.bg +
          ")",
      }}
    >
      <div style={{ marginTop: "auto" }}>
        <div className="cardTitle2">{props.title}</div>
        <div className="cardIntro2">{props.para}</div>
        <div
          className="btn mt-1"
          onClick={() => console.log("Show me clicked")}
        >
          Shop Now
        </div>
      </div>
    </div>
  );
}

export function ItemCard(props) {
  return (
    <>
      <Card
        className=" itemCard col-lg-12 mb-5"
        cover={
          <Carousel autoplay>
            <div>
              <h3
                style={crousalContent}
                style={
                  (crousalContent,
                  {
                    background: `url(https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1584477345-best-jeans-9-1584477328.jpg?crop=1xw:1xh;center,top&resize=480%3A%2A)`,
                  })
                }
              />
            </div>
            <div>
              <h3
                className="crousalContent"
                style={{
                  background: `url(https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1584477345-best-jeans-9-1584477328.jpg?crop=1xw:1xh;center,top&resize=480%3A%2A)`,
                  backgroundPosition: "center",
                  backgroundSize: "cover",
                  backgroundRepeat: "no-repeat",
                }}
              />
            </div>
            <div>
              <h3
                className="crousalContent"
                style={{
                  background: `url(https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1584477345-best-jeans-9-1584477328.jpg?crop=1xw:1xh;center,top&resize=480%3A%2A)`,
                  backgroundPosition: "center",
                  backgroundSize: "cover",
                  backgroundRepeat: "no-repeat",
                }}
              />
            </div>
            <div>
              <h3
                className="crousalContent"
                style={{
                  background: `url(https://hips.hearstapps.com/vader-prod.s3.amazonaws.com/1584477345-best-jeans-9-1584477328.jpg?crop=1xw:1xh;center,top&resize=480%3A%2A)`,
                  backgroundPosition: "center",
                  backgroundSize: "cover",
                  backgroundRepeat: "no-repeat",
                }}
              />
            </div>
          </Carousel>
        }
      >
        <div>
          <div className="itemTitle">Denim Brand Jeans</div>
          <div className="itemPrice ">₹500</div>
          <div className="hide">
            <div style={{ textAlign: "left" }}>
              <h6>
                <span>size:</span> <span className="sizes">34</span>{" "}
                <span className="sizes">36</span>{" "}
                <span className="sizes">38</span>{" "}
              </h6>
            </div>
            <img
              src="https://img.icons8.com/bubbles/50/fa314a/buy.png"
              className="logo1"
            />
          </div>
        </div>
      </Card>
    </>
  );
}
